mapper.select:
select a.*,t.*,s.*,l.*,r.* from activity a  left join activity_type t
	on t.activity_typeId = a.activity_typeId left join stu s
	on s.stu_Id = a.activity_appliStuId  left  join activity_launch l
	on l.activity_lanId = a.activity_launchId left join activity_record r
	on r.activity_recordId = a.activity_recordId
	
			
					 a.activity_Flag = #{aqv.activityInfo.activityFlag}
			
			
					  and a.activity_appliStuId  = #{aqv.activityInfo.studentId}
			
			
					  and a.activity_typeId = #{aqv.activityInfo.activityTypeId}
			
			
					  and a.activity_Name = #{aqv.activityInfo.activityName}
			
	
	order by a.activity_Id 
	limit #{param.startIndex},#{param.rows}

	select a.*,t.*,s.*,l.*,r.* from activity a  left join activity_type t
on t.activity_typeId = a.activity_typeId left join stu s
on s.stu_Id = a.activity_appliStuId  left  join activity_launch l
on l.activity_lanId = a.activity_launchId left join activity_record r
on r.activity_recordId = a.activity_recordId

		
				 a.activity_Flag = #{aqv.activityInfo.activityFlag}
		
		
				  and a.activity_appliStuId  = #{aqv.activityInfo.studentId}
		
		
				  and a.activity_typeId = #{aqv.activityInfo.activityTypeId}
		
		
				  and a.activity_Name = #{aqv.activityInfo.activityName}

select a.*,t.*,s.*,l.*,r.* from activity a left join activity_type t on t.activity_typeId = a.activity_typeId left join stu s on s.stu_Id = a.activity_appliStuId left join activity_launch l on l.activity_lanId = a.activity_launchId left join activity_record r on r.activity_recordId = a.activity_recordId where a.activity_Flag = 0 and a.activity_appliStuId in
#{stuId}
order by a.activity_Id limit #{param.startIndex},#{param.rows}

select * from  activity  a
left join activity_student  s
on a.activity_Id  = s.activity_Id where s.stu_Id   = #{stuId}
