mapper.select:
select stu_Id,stu_Name,stu_Sex,stu_Birthday,stu_Address,stu_BirthPlace.pla_Name,stu_classes.cla_Name,community.community_Name,stu_Photo from stu left join stu_birthplace on stu.stu_BirthPlace = stu_BirthPlace.pla_Id left join stu_classes on stu.stu_ClassId = stu_classes.cla_Id left join community on stu.stu_CommunityId = community_Id
stu_Name like concat('%',concat(#{page.keyWord},'%')) or stu_Address like concat('%',concat(#{page.keyWord},'%')) or stu_classes.cla_Name like concat('%',concat(#{page.keyWord},'%'))
LIMIT #{page.startIndex},#{page.rows}

mapper.update:
UPDATE stu
	SET 
		stu_signature = #{studentSign}
	WHERE
		stu_Id = #{stuId};
